INSERT INTO `news` (`id`, `title`, `text`, `url`, `page_description`, `page_keywords`, `date`, `visible`, `description`, `cover`) VALUES
(5, 'Производство металлоконструкций с полимерно-порошковым покрытием', '<p>Уважаемые клиенты, компания &laquo;Вэлмет&raquo; занимается производством для Вас различных видов металлоизделий, в том числе лёгких и сборно-разборных металлоконструкций. Сварка деталей производится на полуавтоматах в среде защитных газов, что позволяет добиться высокого качества сварных швов.</p>\r\n\r\n<p>Основное преимущество изготавливаемых нами изделий, по сравнению со многими другими производителями, заключается в том, что окрашивание металла у нас происходит, в основном, порошковыми красками с &laquo;запеканием&raquo; эмали в печах полимеризации.</p>\r\n\r\n<p>Покрытие порошковыми эмалями - это современная технология, позволяющая получить &nbsp;эстетичное, прочное, гибкое, устойчивое к коррозии и поэтому долговечное покрытие на металле.</p>\r\n\r\n<p>Наше производство оснащено печами полимеризации размерами 7 х 1,2 х 1 метра для окрашивания колонн, балок, стоек и печью габаритами 5,1 х 2 х 1,5 метра, позволяющей окрашивать относительно крупные металлоконструкции.</p>\r\n\r\n<p>Производство металлоконструкций у нас возможно как по чертежам заказчика, так и по чертежам, разработанных нашими конструкторами. &nbsp;&nbsp;<br />\r\nДля уточнения интересующих Вас вопросов можете обращаться по телефону, указанному в контактах.</p>\r\n', 'polymer-powder', '', '', 1434585600, 1, '', 'Architectural-Industrial-Metal-Finishing-Powder-Coating-12.jpg'),
(6, 'Новый элемент навеса двери', '<p>Нашей компанией &nbsp;разработан новый элемент навеса двери для выпускаемой &nbsp;нами продукции.</p>\r\n\r\n<p>Новая петля кроме более эстетичного вида наших изделий, на которых она применяется, &nbsp;обеспечивает более плавное и бесшумное открывание дверей. &nbsp;Кроме того, благодаря этому небольшому, но важному элементу конструкции, созданы условия, обеспечивающие &nbsp;более долговечную эксплуатацию наших изделий.&nbsp;</p>\r\n', 'doors', '', '', 1426204800, 1, '', 'foto_p.jpg');
